for(var btnBasket=document.getElementsByClassName("btn-buy"),popup=document.querySelector(".popup-add-to-basket"),closeBtn=popup.querySelector(".close-popup-basket"),i=0;i<btnBasket.length;i++)btnBasket[i].addEventListener("click",function(e){e.preventDefault(),popup.classList.add("popup-show")});closeBtn.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("popup-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("popup-show")&&popup.classList.remove("popup-show"))});